    //         setHumidity(data.main.humidity);
    //     })

    //     setLocation('');


    // },[]);


    return ( 
        <div className="container-weather">
        <div className="content">

        <h1 className="title" style={{color:"yellow"}}><strong>Weather widget</strong></h1>

            <h1 className="heading"><strong>Barrie, Ontario, Canada</strong></h1>
            <h1 className="subheading">Weather : {weather}</h1>
            <h2 className="subheading-2">Temprature : {temp}K</h2>
            <h3 className="subheading-2">Feels Like : {feels}K</h3>
            <h4 className="subheading-2">Pressure : {pressure} hPa</h4>
            <h4 className="subheading-2">Humidity : {humidity}%</h4>
        </div>
        </div>

     );
}

export default Weather;
